import React from "react";
import "./styles.css";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import patientData from "./patients.json";
import orderData from "./orders.json";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import SaveIcon from "@material-ui/icons/Save";
import EditIcon from "@material-ui/icons/Edit";
import Dialog from "@material-ui/core/Dialog";
import { Typography } from '@material-ui/core';
import { CardActions } from '@material-ui/core';
import { CardContent } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

export default function App() {
  const patients = patientData;
  const [isOpen, setIsOpen] = React.useState(false);
  const [orderId, setOrderId] = React.useState(0);
  const [isEditable, setIsEditable] = React.useState(false);
  const [prevOrdersData, setOrdersData] = React.useState(orderData);

  const handleEdit = () => {
    setIsEditable(!isEditable);
  };

  const handleClickOpen = (id) => {
    setIsOpen(true);
    setOrderId(id);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const patientList = patients.map((patient, id) => (
    <div class="patientName">
      <Button variant="outlined" key={id} onClick={() => handleClickOpen(id)}>
        {patient.Name}
      </Button>
    </div>
  ));

  function changeToNewOrders(changedMessage, id, ordersData) {
    let newOrdersData = [...ordersData]
    let newOrder = { ...newOrdersData[id] }
    newOrder.Message = changedMessage
    newOrdersData[id] = newOrder
    return { newOrdersData }
  };

  return (
    <div className="App">
      <div class="card">
        <div class="card-header">
          <h1>Patient list and orders</h1>
        </div>
        <div class="card-body">
          <Card>{patientList}</Card>
          <Dialog
            onClose={handleClose}
            open={isOpen}
            aria-labelledby="customized-dialog-title"
            classes={{}}
          >
            <Card className='card' variant="outlined">
              <CardContent>
                <Typography
                  className='cardContent'
                  color="textSecondary"
                  paragraph
                >
                  Editing orders...
                  </Typography>
                <TextField
                  key={orderId}
                  label={patients[orderId].Name}
                  disabled={!isEditable}
                  value={prevOrdersData[orderId].Message}
                  onChange={e => setOrdersData(prevOrdersData => {
                    var _prevOrdersData = prevOrdersData;
                    _prevOrdersData[orderId].Message = e.target.value;
                    return [..._prevOrdersData];
                  })}
                  onBlur={() => setIsEditable(false)}
                />
                <div>{console.log(prevOrdersData)}</div>
              </CardContent>
              <CardActions>
                <Button size="small"
                  variant="contained"
                  onClick={handleEdit}
                  color="primary"
                  startIcon={<EditIcon />}>
                  Edit
                  </Button>
              </CardActions>
            </Card>
          </Dialog>
        </div>
      </div>
    </div>
  );
}
