import React from "react";
import "./styles.css";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import patientData from "./patients.json";
import orderData from "./orders.json";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import SaveIcon from "@material-ui/icons/Save";
import EditIcon from "@material-ui/icons/Edit";
import Dialog from "@material-ui/core/Dialog";

export default function App() {
  const patients = patientData;
  const [isOpen, setIsOpen] = React.useState(false);
  const [dialogId, setDialogId] = React.useState(0);

  const handleClickOpen = (id) => {
    setIsOpen(true);
    setDialogId(id);
    console.log(id);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  var orders = orderData;
  // console.log({ patientData });

  const patientList = patients.map((patient, id) => (
    <div class="patientName">
      <Button variant="outlined" id={id} onClick={() => handleClickOpen(id)}>
        {patient.Name}
      </Button>
      {/* <h3> 病囑: {orders[id].Message}</h3> */}
    </div>
  ));

  // var customDialog = <div />;

  // if (open) {
  //   customDialog =
  //     <Dialog
  //       onClose={handleClose}
  //       open={open}
  //       aria-labelledby="customized-dialog-title"
  //     >
  //       {orders[dialogId].Message}
  //     </Dialog>
  // }

  // function CustomDialog() {
  //   if (open) {
  //     return (
  //       <Dialog
  //         onClose={handleClose}
  //         open={open}
  //         aria-labelledby="customized-dialog-title"
  //       >
  //         {orders[dialogId].Message}
  //       </Dialog>
  //     );
  //   } else {
  //     return <div></div>;
  //   }
  // }

  // const allPatients = () => {};

  return (
    <div className="App">
      <div class="card">
        <div class="card-header">
          <h1>Patient list and orders</h1>
        </div>
        <div class="card-body">
          <div class="card-body-left">
            <Card size="large">{patientList}</Card>
            <Dialog
              onClose={handleClose}
              open={isOpen}
              aria-labelledby="customized-dialog-title"
            >
              {orders[dialogId].message}
            </Dialog>
          </div>
          <div class="card-body-right">
            <div>order</div>
            <div class="order-button">
              <ButtonGroup variant="contained">
                <Button color="primary" startIcon={<EditIcon />}>
                  Edit
                </Button>
                <Button color="secondary" startIcon={<SaveIcon />}>
                  Save
                </Button>
              </ButtonGroup>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
