import React from "react";
import "./styles.css";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import patientData from "./patients.json";
import orderData from "./orders.json";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import SaveIcon from "@material-ui/icons/Save";
import EditIcon from "@material-ui/icons/Edit";
import Dialog from "@material-ui/core/Dialog";
import { Typography } from '@material-ui/core';
import { CardActions } from '@material-ui/core';
import { CardContent } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

export default function App() {
  const patients = patientData;
  const [isOpen, setIsOpen] = React.useState(false);
  const [dialogId, setDialogId] = React.useState(0);
  const [isEditable, setIsEditable] = React.useState(false);
  // const [currentOrder, setCurrentOrder] = React.useState("");
  const [ordersData, setOrdersData] = React.useState(orderData);

  // var orders = orderData;
  // console.log({ patientData });

  const useStyles = makeStyles({
    root: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }
  });
  const classes = useStyles();

  const handleEdit = () => {
    setIsEditable(!isEditable);
  };

  const handleClickOpen = (id) => {
    setIsOpen(true);
    setDialogId(id);
    // setOrdersData(ordersData[id].Message);
    console.log(id);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const patientList = patients.map((patient, id) => (
    <div class="patientName">
      <Button variant="outlined" key={id} onClick={() => handleClickOpen(id)}>
        {patient.Name}
      </Button>
    </div>
  ));

  function changeToNewOrders(changedMessage, id, ordersData) {
    console.log("I am here");
    let newOrdersData = [...ordersData]
    let newOrder = { ...newOrdersData[id] }
    newOrder.Message = changedMessage
    console.log("input: " + (newOrdersData))
    newOrdersData[id] = newOrder
    console.log("data: " + [newOrdersData])
    return { newOrdersData }
  };

  return (
    <div className="App">
      <div class="card">
        <div class="card-header">
          <h1>Patient list and orders</h1>
        </div>
        <div class="card-body">
          <div class="card-body-left">
            <Card size="large">{patientList}</Card>
            <Dialog
              onClose={handleClose}
              open={isOpen}
              aria-labelledby="customized-dialog-title"
              classes={{}}
            // modal = {true}
            // autoDetectWindowHeight={false} 
            // autoScrollBodyContent={false}
            // contentStyle={{width: "100%", maxWidth: "none"}}
            >
              <Card className='card' variant="outlined">
                <CardContent>
                  <Typography
                    className='cardContent'
                    color="textSecondary"
                    paragraph
                  >
                    Editing orders...
                  </Typography>
                  <TextField
                    key={dialogId}
                    label={patients[dialogId].Name}
                    disabled={!isEditable}
                    value={ordersData[dialogId].Message}
                    // onChange={(e) => setOrdersData(e.target.value)}
                    onChange={(e) => setOrdersData(changeToNewOrders(e.target.value, dialogId, ordersData))}
                    onBlur={() => setIsEditable(false)}
                  />
                  <div>input = {ordersData[dialogId].Message}</div>
                  <div>{console.log(ordersData)}</div>
                </CardContent>
                <CardActions>
                  <Button size="small"
                    variant="contained"
                    onClick={handleEdit}
                    color="primary"
                    startIcon={<EditIcon />}>
                    Edit
                  </Button>
                </CardActions>
              </Card>
            </Dialog>
          </div>
        </div>
      </div>
    </div>
  );
}
