import React from "react";
import "./styles.css";
import Button from "@material-ui/core/Button";
import Card from "@material-ui/core/Card";
import patientData from "./patients.json";
import orderData from "./orders.json";
import ButtonGroup from "@material-ui/core/ButtonGroup";
import SaveIcon from "@material-ui/icons/Save";
import EditIcon from "@material-ui/icons/Edit";
import Dialog from "@material-ui/core/Dialog";
import { Typography } from '@material-ui/core';
import { CardActions } from '@material-ui/core';
import { CardContent } from '@material-ui/core';
import { TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { positions } from '@material-ui/system';

export default function App() {
  const patients = patientData;
  const [isOpen, setIsOpen] = React.useState(false);
  const [orderId, setOrderId] = React.useState(0);
  const [isEditable, setIsEditable] = React.useState(false);
  const [prevOrdersData, setOrdersData] = React.useState(orderData);

  const useStyles = makeStyles({
    root: {
      background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
      opacity: '90%',
      borderRadius: 5,
      border: 0,
      color: 'white',
      height: 48,
      padding: '0 30px',
      boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
      fontWeight: 'bold',
      transition: "0.1s cubic-bezier(.17, .67, .83, .67)",
      '&:hover': {
        background: "#fff",
        color: "rgb(39,39,39)",
        transform: "scale(1.1) translateY(-3px)",
        boxShadow: '0 3px 5px 2px rgba(232, 232, 232, .3)',
      },
    },
    cards: {
      backgroundColor: 'transparent',
      width: '100%',
      height: '100%',
      overflow: 'true',
      margin: '0 30px',
    },
    buttonPos: {
      top: "5 px",
      right: "5 px",
      position: "absolute",
      alignItems: "flex-right",
      flexDirection: "row",
    },
    dialogFlex: {
      display: "flex",
      flexDirection: "column",
      alignItems: "flex-right",
      position: "relative",
    }
  });

  const classes = useStyles();

  const handleEdit = () => {
    setIsEditable(!isEditable);
  };

  const handleClickOpen = (id) => {
    setIsOpen(true);
    setOrderId(id);
  };

  const handleClose = () => {
    setIsOpen(false);
  };

  const patientList = patients.map((patient, id) => (
    <div className="patientName">
      <Button className="patientButton" variant="outlined" size="large"
        classes={{
          root: classes.root,
          cardHovered: classes.root,
        }}
        key={id} onClick={() => handleClickOpen(id)}>
        {patient.Name}
      </Button>
    </div>
  ));

  function changeToNewOrders(changedMessage, id, ordersData) {
    let newOrdersData = [...ordersData]
    let newOrder = { ...newOrdersData[id] }
    newOrder.Message = changedMessage
    newOrdersData[id] = newOrder
    return { newOrdersData }
  };

  return (
    <div className="App">
      <div class="card">
        <div class="card-header">
          <h1>Patient list and orders</h1>
        </div>
        <div class="card-body">
          <div className="patientList">
            {patientList}
          </div>
          <Dialog
            onClose={handleClose}
            open={isOpen}
            aria-labelledby="customized-dialog-title"
            classes={{
              dialogFlex: classes.dialogFlex,
            }}
          >
            <Card className='card' variant="outlined">
              <CardActions
                classes={{
                  buttonPos: classes.buttonPos,
                }}>
                <Button size="small"
                  variant="contained"
                  onClick={handleEdit}
                  color="primary"
                  startIcon={<EditIcon />}
                  top="5px"
                >
                  Edit
                  </Button>
              </CardActions>
              <CardContent>
                <Typography
                  className='cardContent'
                  color="textSecondary"
                  paragraph
                >
                  Editing orders...
                  </Typography>

                <TextField
                  key={orderId}
                  label={patients[orderId].Name}
                  disabled={!isEditable}
                  value={prevOrdersData[orderId].Message}
                  onChange={e => setOrdersData(prevOrdersData => {
                    var _prevOrdersData = prevOrdersData;
                    _prevOrdersData[orderId].Message = e.target.value;
                    return [..._prevOrdersData];
                  })}
                  onBlur={() => setIsEditable(false)}
                />
                <div>{console.log(prevOrdersData)}</div>
              </CardContent>

            </Card>
          </Dialog>
        </div>
      </div>
    </div>
  );
}
